mapper.select:
select *
from GROUPS where g_status = '1' order by g_idx desc limit #{limit}
offset #{offset}

SELECT * FROM GROUPS
WHERE g_status = '1'

	AND g_title LIKE CONCAT('%', #{g_title}, '%')


	AND g_cdo = #{g_cdo}


	AND g_gugun = #{g_gugun}

ORDER BY g_idx DESC

SELECT COUNT(*) FROM GROUPS
WHERE g_status = '1'

	AND g_title LIKE CONCAT('%', #{g_title}, '%')


	AND g_cdo = #{g_cdo}


	AND g_gugun = #{g_gugun}

select count(*)
from GROUPUSERS
where g_idx = #{g_idx} and u_idx = #{u_idx}

mapper.update:
update GROUPS
set g_status= '0'
where g_idx = #{g_idx}

update GROUPS
set g_status= '1'
where g_idx = #{g_idx}

mapper.select:
SELECT *
FROM GROUPS
WHERE g_cdo LIKE #{input}
   OR g_gugun LIKE #{input}
   OR g_location LIKE #{input}
